mapper.insert:

  SELECT qna_seq.NEXTVAL FROM dual

INSERT INTO qna (
  QnaNum, memberNum, QnADate, QnATitle, QnAText, QnAState, QnAreply
) VALUES (
  #{qnaNum}, #{memberNum}, SYSDATE, #{qnaTitle}, #{qnaText}, 0, NULL
)

mapper.update:
 UPDATE qna
 SET qnaReply = #{qnaReply}  
where qnaNum = #{qnaNum}
